steps = instructions.chars
current_floor = 0

steps.each_with_index do |step, idx|
  step == "(" ? (current_floor += 1) : (current_floor -= 1)
  if current_floor.negative?
     puts("Santa needed #{idx + 1} steps to reach the basement")
     break
  end
end
